import React, { useState } from 'react';
import { createWorker } from 'tesseract.js';
import './App.css';

const App = () => {
  const [result, setResult] = useState('');
  const [capturing, setCapturing] = useState(false);

  const performOCR = async () => {
    if (capturing) return;

    setCapturing(true);
    try {
      const response = await fetch('/api/perform-ocr');
      const data = await response.json();
      setResult(data.result);
    } catch (error) {
      console.error('Error performing OCR:', error);
    } finally {
      setCapturing(false);
    }
  };

  const saveToAudio = async () => {
    if (!result) return;

    try {
      const response = await fetch('/api/save-to-audio', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: result }),
      });

      if (response.ok) {
        // Handle audio file download or any other action here
        console.log('Audio file saved successfully.');
      }
    } catch (error) {
      console.error('Error saving to audio:', error);
    }
  };

  return (
    <div className="App">
      <h1>OCR Web App</h1>
      <button onClick={performOCR} disabled={capturing}>
        Perform OCR
      </button>
      <br />
      <textarea
        value={result}
        onChange={(e) => setResult(e.target.value)}
        placeholder="OCR Result"
      ></textarea>
      <br />
      <button onClick={saveToAudio} disabled={!result}>
        Save to Audio
      </button>
    </div>
  );
};

export default App;
